<script setup lang="ts">
const props = defineProps({
  isInfinite: {
    type: Boolean,
    default: false,
  },
})
const elClass = ref('rotation')
if (props.isInfinite) {
  elClass.value += ' rotation-infinite'
}

const startAnimation = function () {
  if (!props.isInfinite) {
    if (elClass.value === 'rotation') return
    elClass.value = 'rotation'
    setTimeout(() => {
      elClass.value = ''
    }, '4000')
  }
}
onMounted(() => {
  if (!props.isInfinite) {
    setTimeout(() => {
      elClass.value = ''
    }, '4000')
  }
})
</script>

